import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { UserContext } from '../../context/UserContext';
import { useHistory } from 'react-router-dom';

const ScrumDetails = ({ scrum }) => {
    const [tasks, setTasks] = useState([]);
    const [users, setUsers] = useState([]);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user) {
            navigate('/login');
        }
    }, [user, navigate]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get(`http://localhost:4000/tasks?scrumId=${scrum.id}`);
                setTasks(response.data.map(task => ({
                    ...task,
                    id: Math.abs(Number(task.id)), // Ensure positive IDs
                    assignedTo: Math.abs(Number(task.assignedTo)), // Ensure positive IDs
                })));
            } catch (error) {
                console.error('Error fetching tasks:', error);
                alert('Failed to fetch tasks. Please try again later.');
            }
        };

        fetchTasks();
    }, [scrum.id]);

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:4000/users');
                setUsers(response.data.map(user => ({
                    ...user,
                    id: Math.abs(Number(user.id)), // Ensure positive IDs
                })));
            } catch (error) {
                console.error('Error fetching users:', error);
                alert('Failed to fetch users. Please try again later.');
            }
        };

        fetchUsers();
    }, []);

    const handleStatusChange = async (taskId, newStatus) => {
        try {
            const taskToUpdate = tasks.find(task => task.id === Number(taskId));
            if (!taskToUpdate) {
                console.error(`Task with ID ${taskId} not found`);
                alert(`Task with ID ${taskId} not found.`);
                return;
            }

            const updatedHistory = [
                ...taskToUpdate.history,
                {
                    status: newStatus,
                    date: new Date().toISOString().split('T')[0],
                },
            ];

            await axios.patch(`http://localhost:4000/tasks/${taskId}`, {
                status: newStatus,
                history: updatedHistory,
            });

            // Update state with new status and history
            setTasks(prevTasks =>
                prevTasks.map(task =>
                    task.id === Number(taskId)
                        ? { ...task, status: newStatus, history: updatedHistory }
                        : task
                )
            );
        } catch (error) {
            console.error('Error updating task status:', error);
            alert('Failed to update task status. Please try again later.');
        }
    };

    const assignedUsers = users.filter(user => tasks.some(task => task.assignedTo === user.id));

    return (
        <div>
            <h3>Scrum Details for {scrum.name}</h3>
            <h4>Tasks</h4>
            <ul>
                {tasks.map(task => (
                    <li key={task.id}>
                        <strong>{task.title}:</strong> {task.description} - <em>{task.status}</em>
                        {user?.role === 'admin' && (
                            <select
                                value={task.status}
                                onChange={(e) => handleStatusChange(task.id, e.target.value)}
                            >
                                <option value="To Do">To Do</option>
                                <option value="In Progress">In Progress</option>
                                <option value="Done">Done</option>
                            </select>
                        )}
                    </li>
                ))}
            </ul>
            <h4>Users</h4>
            <ul>
                {assignedUsers.map(user => (
                    <li key={user.id}>
                        {user.name} ({user.email})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ScrumDetails;
